const Example = () => {
  const [val, setVal] = useState('');
  const clearVal = () => setVal('');
  return (
    <div>
      <label htmlFor="456">ラベル</label>
      <div>
        <input
          id="123"
          placeholder="こんにちは"
          value={val}
          onChange={(e) => setVal(e.target.value)}
        />
        <textarea
          id="456"
          placeholder="こんにちは"
          value={val}
          onChange={(e) => setVal(e.target.value)}
        />
      </div>
      <h3>{val}</h3>
      <button Onclick={clearVal}>クリア</button>
    </div>
  );
};

export default Example;
